int score;    //initialize value according to question

char grade;



switch(score/10) {



        case 10: grade = 'A';

        case  9: grade = 'A'; break;

        case  8: grade = 'B';

        case  7: grade = 'C';

        case  6: grade = 'C'; break;

        case  5: grade = 'D';

        default: grade = 'F';

}
